from collections import defaultdict

"""
abcd
a abc b cd

aaaababaaaaab
a aa aab

All you need to do is prove that every index can
be reached somehow and that a result ends up
falling directly on the last element in the string
"""
def word_break(s, wordDict):
    s_len = len(s)
    word_dict = defaultdict(list)
    for word in wordDict:
        word_dict[word[0]].append(word)

    def is_solvable(index):
        if index == s_len:
            return True

        if index > s_len:
            return False

        possibilites = word_dict[s[index]]
        for possibility in possibilites:
            possibility_len = len(possibility)
            if possibility == s[index: index + possibility_len] and is_solvable(index + possibility_len):
                return True
        return False

    return is_solvable(0)


def word_break_memoize(s, wordDict):
    s_len = len(s)
    word_dict = defaultdict(list)
    for word in wordDict:
        word_dict[word[0]].append(word)

    cache = {}
    def is_solvable(index):
        if index in cache:
            return cache[index]

        val = False
        if index == s_len:
            val = True
        elif index < s_len:
            possibilites = word_dict[s[index]]
            for possibility in possibilites:
                possibility_len = len(possibility)
                if possibility == s[index: index + possibility_len] and is_solvable(index + possibility_len):
                    val = True
                    break

        cache[index] = val
        return val

    return is_solvable(0)


"""
abcd
a abc b cd

aaaababaaaaab
a aa aab

"""

def work_break_tableu(s, wordDict):
    s_len = len(s)
    word_dict = defaultdict(list)
    for word in wordDict:
        word_dict[word[0]].append(word)

    cache = [False for _ in range(s_len + 1)]
    cache[0] = s[0] in word_dict

    for i in range(s_len):
        if not cache[i]:
            continue
        possibilites = word_dict[s[i]]
        for possibility in possibilites:
            possibility_len = len(possibility)
            if s[i:i+possibility_len] == possibility:
                cache[i+possibility_len] = True
    return cache[-1]



